import { useEffect, useState } from "react";

import Typography from '@mui/material/Typography';
import Pagination from '@mui/material/Pagination';
import TextField from '@mui/material/TextField';

import AnimeCard from '../../components/AnimeCard';
import { styContainer, styHeader } from './styles';

const  LIMIT_PERPAGE = 12;

const Home = () => {
  const [page, setPage] = useState(1);
  const [animeList, setAnimeList] = useState([])
  const [lastVisiblePage, setLastVisiblePage] = useState(0);
  const [keyword, setKeyword] = useState('');

  const debounce = (func, timeout = 300) => {
    let timer;
    return (...args) => {
      clearTimeout(timer);
      timer = setTimeout(() => { func.apply(this, args); }, timeout);
    };
  };

  const saveInput = () =>{
    console.log('Saving data');
  }

  const processChange = debounce(() => saveInput());

  const handleChangePage = (event: React.ChangeEvent<unknown>, value: number) => {
    setPage(value);
  };

  const handleChangeKeyWord = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const val = e.target.value;
    setKeyword(val);
  };


  useEffect(() => {
    fetch(`https://api.jikan.moe/v4/anime?page=${page}&limit=${LIMIT_PERPAGE}`)
      .then((response) => response.json())
      .then((data) => {
        setAnimeList(data.data);
        setLastVisiblePage(data.pagination.last_visible_page)
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [page]);

  return(
    <>
      <div className={styHeader}>
        <Typography gutterBottom variant="h5" component="div">
          Anime List
        </Typography>

        <Typography gutterBottom variant="h5" component="div">
          Favorites
        </Typography>
      </div>
      <div className={styContainer}>
        <TextField value={keyword} onKeyup={processChange} onChange={(e) =>handleChangeKeyWord(e)} placeholder="Search Anime" variant="outlined" />

        <div className="sty-anime-container">
          {animeList?.map((anime, index) => {
            return(
              <>
                <AnimeCard key={index} anime={anime} />
              </>
            )
          })}
        </div>

        <Pagination className="sty-pagination" count={lastVisiblePage} page={page} onChange={handleChangePage} />

      </div>
    </>
  )

}

export default Home;
