import { useEffect, useState } from "react";
import { Waypoint } from 'react-waypoint';

import Pagination from '@mui/material/Pagination';

import AnimeCard from '../../components/AnimeCard'

const Home = () => {
  const [page, setPage] = useState(1);
  const [animeList, setAnimeList] = useState([])
  const [lastVisiblePage, setLastVisiblePage] = useState(0);

  // const handleOnScroll = () => {
  //   setPage( page + 1 );
  // }

  useEffect(() => {
    fetch(`https://api.jikan.moe/v4/anime?page=${page}&limit=10`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setAnimeList(data.data);
        setLastVisiblePage(data.pagination.last_visible_page)
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [page]);

  const handleChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setPage(value);
  };

  return(
    <div>
      {animeList?.map((anime, index) => {
        // const isLastCard = index === animeList.length - 1;
        return(
          <>
            <AnimeCard key={index} anime={anime} />

            {/* {isLastCard && <Waypoint onEnter={handleOnScroll} />} */}
          </>
        )
      })}

      <Pagination count={lastVisiblePage} page={page} onChange={handleChange} />

    </div>
  )

}

export default Home;
