import { useEffect, useState } from "react";

import Pagination from '@mui/material/Pagination';

import AnimeCard from '../../components/AnimeCard';
import {styContainer} from './styles';

const  LIMIT_PERPAGE = 12;

const Home = () => {
  const [page, setPage] = useState(1);
  const [animeList, setAnimeList] = useState([])
  const [lastVisiblePage, setLastVisiblePage] = useState(0);


  useEffect(() => {
    fetch(`https://api.jikan.moe/v4/anime?page=${page}&limit=${LIMIT_PERPAGE}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setAnimeList(data.data);
        setLastVisiblePage(data.pagination.last_visible_page)
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [page]);

  const handleChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setPage(value);
  };

  return(
    <div className={styContainer}>
      <div className='sty-header'>

      </div>
      <div className="sty-anime-container">
        {animeList?.map((anime, index) => {
          return(
            <>
              <AnimeCard key={index} anime={anime} />
            </>
          )
        })}
      </div>

      <Pagination className="sty-pagination" count={lastVisiblePage} page={page} onChange={handleChange} />

    </div>
  )

}

export default Home;
