import { css } from '@emotion/css';

export const styContainer= css`
  width: 80%;
  margin: 0 auto 24px;

  .sty-anime-container {
    display: grid;
    grid-template-columns: repeat(12, minmax(0px, 1fr));
    gap: 1rem;
  }

  .sty-pagination {
    display: flex;
    justify-content: flex-end;
    margin-top: 16px;
  }
`

export const styHeader = css`
  display: flex;
  justify-content: space-between;
  padding: 16px 24px;
  border-bottom: 1px solid #e0e0e0;
  margin-bottom: 24px;

  & > div {
    cursor: pointer;
    margin: 0;

    &:hover {
      color: blue;
    }
  }
`
